import React, { useState } from 'react';

export default function KeyValueControls({ db, setDb }) {
  const [key, setKey] = useState('');
  const [value, setValue] = useState('');
  const [queryKey, setQueryKey] = useState('');
  const [queryResult, setQueryResult] = useState('');

  const handlePut = () => {
    setDb(prev => ({ ...prev, [key]: value }));
    setKey('');
    setValue('');
  };

  const handleGet = () => {
    setQueryResult(db[queryKey] || 'Key not found');
  };

  return (
    <div>
      <h2>Add Entry</h2>
      <input value={key} onChange={e => setKey(e.target.value)} placeholder="Key" />
      <input value={value} onChange={e => setValue(e.target.value)} placeholder="Value" />
      <button onClick={handlePut}>Add</button>

      <h2>Get Value</h2>
      <input value={queryKey} onChange={e => setQueryKey(e.target.value)} placeholder="Key" />
      <button onClick={handleGet}>Get</button>
      <p>Value: {queryResult}</p>

      {/* <h2>All Key-Value Pairs</h2>
      <pre>{JSON.stringify(db, null, 2)}</pre> */}
    </div>
  );
}
